from robot.api import logger

'''
日志打印
'''


class WnConfig(object):
    ROBOT_LIBRARY_SCOPE = "GLOBAL"

    def wn_get_config(self, file_path):
        config = []
        with open(file_path, 'r', encoding='utf-8') as f:
            while True:
                line = f.readline()
                if not line:
                    break
                line = line.strip('\n').strip('\r').strip()
                if line.startswith('#') or not line:
                    continue
                try:
                    line_split = line.split('=', 1)
                    config.append([line_split[0], line_split[1]])
                except Exception as e:
                    logger.console('error config:' + line)
                    logger.console('error message:' + str(e))
        return config


if __name__ == "__main__":
    WnConfig().wn_get_config()
